,p_plug_display_sequence=>10
,p_plug_grid_column_span=>3
,p_plug_display_column=>1
,p_plug_display_point=>'BODY'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select * from(',
'select case when connect_by_isleaf = 1 then 0',
'            when level = 1             then 1',
'            else                           -1',
'       end as status, ',
'       level, ',
'       label||'': ''||name as title, ',
'       case when item_type = ''TT'' then ''fa-file-text-o''',
'            when item_type = ''AG'' then ''fa-caret-square-o-right''',
'            when item_type = ''RL'' then ''fa-minus-square-o''            ',
'       else null',
'       end as icon, ',
'       id as value, ',
'       query_string,',
'       case when tooltip is not null then name',
'            else name',
'       end as tooltip,       ',
'       case ',
'            when item_type = ''AG'' then                              ',
'                apex_util.prepare_url(''f?p=''||:app_id||'':3:''||:app_session||					',
'                    ''::::P3_SELECTED_NODE,P3_APPROVAL_GROUP_ID,P3_RL_AG_APPL_ID,P3_RL_AG_ID:''||id||'',''||substr(id,1,instr(id,''-'',1)-1)||'',''||parent||'',''||substr(id,1,instr(id,''-'',1)-1))',
'       end as link ',
' from (',
'select ''TT'' item_type,',
'       t.label label,',
'       to_char(a.application_id) id,',
'       null parent,       ',
'       a.application_name name,',
'       null query_string,',
'       a.TRANSACTION_TYPE_ID tooltip,',
'       null link',
'  from apps.ame_calling_apps_vl a, (select wwv_flow_lang.system_message(''Transaction Type'') label from dual) t',
'  where  /* application_name in (''EAM Work Request Approval'',''Purchase Order Approval'',''Purchase Requisition Approval'',''Payables Holds Resolution''',
'                             ,''Internal Requisition Approval''',
'                             ) */',
'     1 = 1',
'  and application_name in   (select meaning ',
'                             from   fnd_lookup_values',
'                             where  lookup_type= ''XXAPEX_AME_TRANSACTION_TYPES''',
'                             and    enabled_flag = ''Y''',
'                             and    trunc(sysdate) between trunc(start_date_active) and trunc(nvl(end_date_active,sysdate))',
'                            )   ',
'  and     sysdate between a.start_date and nvl(a.end_date - (1/86400),sysdate)',
'  and  upper(NVL2(:P3_TRX_TYPE,a.application_name,1)) like upper(NVL2(:P3_TRX_TYPE,''%''||:P3_TRX_TYPE||''%'',1))    ',
'  and   a.application_id in ',
'		(',
'		   select  apps.application_id',
'			FROM apps.ame_rules_vl rul,',
'				 apps.ame_rule_usages rlu,',
'				 apps.ame_action_usages acu,',
'				 apps.ame_actions act,',
'				 apps.ame_action_types aty,',
'				 apps.ame_calling_apps_vl apps ',
'		   WHERE SYSDATE BETWEEN apps.start_date',
'							 AND NVL (apps.end_date - (1 / 86400), SYSDATE)',
'				 AND SYSDATE BETWEEN aty.start_date',
'								 AND NVL (aty.end_date - (1 / 86400), SYSDATE)',
'				 AND SYSDATE BETWEEN act.start_date',
'								 AND NVL (act.end_date - (1 / 86400), SYSDATE)',
'				 AND (SYSDATE BETWEEN acu.start_date',
'								  AND NVL (acu.end_date - (1 / 86400), SYSDATE)',
'					  OR (acu.start_date > SYSDATE',
'						  AND acu.end_date > acu.start_date))',
'				 AND (SYSDATE BETWEEN rul.start_date',
'								  AND NVL (rul.end_date - (1 / 86400), SYSDATE)',
'					  OR (rul.start_date > SYSDATE',
'						  AND rul.end_date > rul.start_date))',
'				 AND (SYSDATE BETWEEN rlu.start_date',
'								  AND NVL (rlu.end_date - (1 / 86400), SYSDATE)',
'					  OR (rlu.start_date > SYSDATE',
'						  AND rlu.end_date > rlu.start_date))',
'				 AND rlu.item_id = apps.application_id',
'				 AND rul.rule_id = rlu.rule_id',
'				 AND acu.rule_id = rul.rule_id',
'				 AND acu.action_id = act.action_id',
'				 AND act.action_type_id = aty.action_type_id',
'				 AND aty.name IN',
'						(''post-chain-of-authority approvals'',',
'						 ''pre-chain-of-authority approvals'',',
'						 ''approval-group chain of authority'')',
'				 and     upper(NVL2(:P3_RULE_NAME,rul.description,1)) like upper(NVL2(:P3_RULE_NAME,''%''||:P3_RULE_NAME||''%'',1)) 	',
'		)',
'	and   a.application_id in ',
'		(',
'				select    agc.application_id',
'				from      apps.AME_APPROVAL_GROUPS_VL          agv',
'						  ,apps.AME_APPROVAL_GROUP_CONFIG    agc ',
'				where   agv.APPROVAL_GROUP_ID = agc.APPROVAL_GROUP_ID',
'				AND     SYSDATE BETWEEN agv.start_date AND NVL ( agv.end_date - (1 / 84600), SYSDATE)',
'				AND     SYSDATE BETWEEN agc.start_date  AND NVL (agc.end_date- (1 / 84600), SYSDATE)',
'				and     upper(NVL2(:P3_QRY_SRTING,query_string,1)) like upper(NVL2(:P3_QRY_SRTING,''%''||:P3_QRY_SRTING||''%'',1)) ',
'				and     upper(NVL2(:P3_ROLE_1,query_string,1)) like upper(NVL2(:P3_ROLE_1,''%''||:P3_ROLE_1||''%'',1)) ',
'				and     upper(NVL2(:P3_ROLE_2,query_string,1)) like upper(NVL2(:P3_ROLE_2,''%''||:P3_ROLE_2||''%'',1))',
'				and     upper(NVL2(:P3_ROLE_3,query_string,1)) like upper(NVL2(:P3_ROLE_3,''%''||:P3_ROLE_3||''%'',1)) ',
'				and     upper(NVL2(:P3_ROLE_4,query_string,1)) like upper(NVL2(:P3_ROLE_4,''%''||:P3_ROLE_4||''%'',1))',
'				and     upper(NVL2(:P3_APPROVER_GROUP,agv.NAME,1)) like upper(NVL2(:P3_APPROVER_GROUP,''%''||:P3_APPROVER_GROUP||''%'',1))  ',
'		)',
'union all',
'select    ''AG'' item_type,',
'       u.label label,       ',
'       to_char(agv.approval_group_id)||''-''||to_char(agc.application_id) id,',
'       to_char(agc.application_id) parent,',
'       agv.NAME name,',
'       agv.query_string,',
'       null tooltip,',
'       agv.approval_group_id link          ',
'from        apps.AME_APPROVAL_GROUPS_VL          agv',
'           ,apps.AME_APPROVAL_GROUP_CONFIG    agc , (select wwv_flow_lang.system_message(''Approver Group'') label from dual) u',
'where   agv.APPROVAL_GROUP_ID = agc.APPROVAL_GROUP_ID',
'AND     SYSDATE BETWEEN agv.start_date AND NVL ( agv.end_date - (1 / 84600), SYSDATE)',
